    //     const handleDeepLink = (url) => {
    //         console.log('url', url)
    //         if (url && url.startsWith('myapp://HomeScreen')) {
    //             navigation.navigate('HomeScreen');
    //         }
    //     };

    //     Linking.addEventListener('url', ({ url }) => handleDeepLink(url));

