                headers: {
                    'Content-Type': 'application/json',
                    'consumer_key': '3b137de2b677819b965ddb7288bd73f62fc6c1f04a190678ca6e72fca3986629',
                }
            }
        ).then((res) => {
            setSingleProduct(res.data)
            setTimeout(() => {
                setLoading(false);
            }, 2000);
        })
    }

    const ratingCompleted = (rating) => {
        setStar(rating);
    }

